import React, { useState, useEffect } from 'react';
import EditStudent from './EditStudent'; 

const AdminHome = () => {
    const [students, setStudents] = useState([]);
    const [newStudentName, setNewStudentName] = useState('');
    const [newStudentEmail, setNewStudentEmail] = useState('');
    const [message, setMessage] = useState('');
    const [selectedStudentId, setSelectedStudentId] = useState(null);
    const [showEditStudent, setShowEditStudent] = useState(false);

    useEffect(() => {

        fetchStudents();
    }, []);
    const toggleEditStudent = () => {
      setShowEditStudent(!showEditStudent);
  };
  const headers = [' ', 'Year', 'Semester'];

    const fetchStudents = (studentId) => {
        const endpoint = `http://localhost:8080/student`;

        fetch(endpoint)
            .then((response) => response.json())
            .then((data) => {
                setStudents(data); 
            })
            .catch((err) => {
                console.log("exception fetchStudents " + err);

                setMessage("Exception. " + err);
            });
    };

    const handleAddStudent = () => {
        const emailFormat = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailFormat.test(newStudentEmail)) {
            setMessage('Must be Valid Email');
            return;
        }


        if (!students.every((student) => student.email !== newStudentEmail)) {
            setMessage('Email Already in Use');
            return;
        }

        const nextStudentId = students.length + 1;

        const newStudent = {
            studentId: nextStudentId,
            name: newStudentName,
            email: newStudentEmail,
            statusCode: 0, 
            status: null, 
        };


        setStudents([...students, newStudent]);
        //Clear Everything
        setNewStudentName('');
        setNewStudentEmail('');
        setMessage('');
    };

    const handleDeleteStudent = (studentId) => {
      const student = students.find((student) => student.id === studentId);

      if (!student) {
          setMessage('Student not found');
          return;
      }

      if (student.enrollments && student.enrollments.length > 0) {
          const confirmMessage = `This student has ${student.enrollments.length} enrollments. Are you sure you want to delete them?`;

          if (window.confirm(confirmMessage)) {
              // Pass the "force" parameter when deleting
              deleteStudent(studentId, 'force');
          }
      } else {
          // No enrollments, delete without "force" parameter
          deleteStudent(studentId);
      }
  };

  const deleteStudent = (studentId) => {
    const endpoint = `http://localhost:8080/student/${studentId}`;

    console.log("Deleting student with ID:", studentId);

    fetch(endpoint, { method: 'DELETE' })
        .then((response) => {
            console.log("Delete response:", response);
            if (response.ok) {
                const updatedStudents = students.filter((student) => student.studentId !== studentId);
                setStudents(updatedStudents);
            } else {
                setMessage('Failed to delete student');
            }
        })
        .catch((err) => {
            console.log("exception deleteStudent " + err);
            setMessage("Exception. " + err);
        });
};

    return (
        <div>
            <div style={{ textAlign: 'center' }}>
                <h3>Student List</h3>
                <button type="button" onClick={() => fetchStudents()}>
                    Get
                </button>
            </div>

            {/* Display the list of students */}
            <ul>
                {students.map((student, index) => (
                    <li key={index}>
                        {student.name} - {student.email}
                        <button type="button" onClick={() => handleDeleteStudent(parseInt(student.id))}>Delete</button>
                    </li>
                ))}
            </ul>

            <div>
                <h3>Add Student</h3>
                <input
                    type="text"
                    placeholder="Student Name"
                    value={newStudentName}
                    onChange={(e) => setNewStudentName(e.target.value)}
                />
                <input
                    type="email"
                    placeholder="Student Email"
                    value={newStudentEmail}
                    onChange={(e) => setNewStudentEmail(e.target.value)}
                />
                <button type="button" onClick={handleAddStudent}>
                    Add
                </button>
            </div>

            {message && <div>{message}</div>}
        </div>
    );
};

export default AdminHome;
