import React, { useState } from 'react';

const EditStudent = (props) => {
  // Destructure the student object from props
  const { studentId, name, email, status } = props.student;

  // Create state variables to hold the edited values
  const [editedID, setEditedID] = useState(status); // Updated state variable for status
  const [editedName, setEditedName] = useState(name);
  const [editedEmail, setEditedEmail] = useState(email);
  const [editedStatus, setEditedStatus] = useState(status); // Updated state variable for status

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Example: Update the student object in the parent component's state
    const updatedStudent = {
      studentId: editedID,
      name: editedName,
      email: editedEmail,
      status: editedStatus, // Update the status with the edited value
    };

    // Call a function to update the student data in the parent component
    props.onEdit(updatedStudent);
  };

  return (
    <div>
      <h2>Edit Student</h2>
      <form onSubmit={handleSubmit}>
      <div>
          <label>ID:</label>
          <input
            type="int"
            value={editedID}
            onChange={(e) => setEditedID(e.target.value)}
          />
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={editedName}
            onChange={(e) => setEditedName(e.target.value)}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={editedEmail}
            onChange={(e) => setEditedEmail(e.target.value)}
          />
        </div>

        <div>
          <label>Status:</label> {/* Updated label to "Status" */}
          <input
            type="text"
            value={editedStatus}
            onChange={(e) => setEditedStatus(e.target.value)}
          />
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditStudent;
