import React, { useState, useEffect } from 'react';
import EditStudent from './EditStudent';
import {SERVER_URL, SEMESTERS} from '../constants';


const AdminHome = () => {
  const [students, setStudents] = useState([]);
  const [newStudentName, setNewStudentName] = useState('');
  const [newStudentEmail, setNewStudentEmail] = useState('');
  const [message, setMessage] = useState('');
  const [editedStudent, setEditedStudent] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    const endpoint = 'http://localhost:8080/student';

    fetch(endpoint)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch students');
        }
        return response.json();
      })
      .then((data) => {
        setStudents(data);
      })
      .catch((err) => {
        console.error('Error fetching students:', err.message);
        setMessage('Failed to fetch students. ' + err.message);
      });
  };

  const handleAddStudent = () => {


    if (students.some((student) => student.email === newStudentEmail)) {
      setMessage('Email Already in Use');
      return;
    }

    const nextStudentId = students.length + 1;

    const newStudent = {
      studentId: nextStudentId,
      name: newStudentName,
      email: newStudentEmail,
      statusCode: 0, // Set an appropriate value for statusCode if needed
      status: 'Pending', // Set an appropriate value for status if needed
    };

    setStudents([...students, newStudent]);

    // Clear the input fields
    setNewStudentName('');
    setNewStudentEmail('');

    // Clear any previous error message
    setMessage('');
  };

  const handleDeleteStudent = (studentId) => {
    const studentIndex = students.findIndex((student) => student.studentId === studentId);

    if (studentIndex === -1) {
      // Student not found
      setMessage('Student not found.');
      return;
    }

    const updatedStudents = [...students.slice(0, studentIndex), ...students.slice(studentIndex + 1)];

    if (window.confirm('Are you sure you want to delete the student')) {
      fetch(`${SERVER_URL}/schedule/${studentId}`, {
        method: 'DELETE',
      })
        .then((res) => {
          if (res.ok) {
            console.log('Delete successful');
            setStudents(updatedStudents);
            setMessage('Student deleted.');
          } else {
            console.log('Delete error:', res.status);
            setMessage('Error deleting student. ' + res.status);
          }
        })
        .catch((err) => {
          console.error('Exception:', err);
          setMessage('Exception. ' + err.message);
        });
    }
  };



  const handleEditStudent = (student) => {
    setEditedStudent(student);
  };

  const handleEditSubmit = (editedStudentData) => {
    // Handle the submission of edited student data here, e.g., send it to an API or update it in your state
    console.log('Updated Student Data:', editedStudentData);

    // Find the index of the edited student in the students array
    const index = students.findIndex((student) => student.studentId === editedStudentData.studentId);

    if (index !== -1) {
      // Create a new array with the edited student data
      const updatedStudents = [...students];
      updatedStudents[index] = editedStudentData;

      // Update the students state with the new array
      setStudents(updatedStudents);
    }

    // You can also close the edit form if needed
    setEditedStudent(null);
  };

  return (
    <div>
      <h3>Student List</h3>
      <ul>
        {students.map((student, index) => (
          <li key={index}>
            {student.name} - {student.email}
            <button type="button" onClick={() => handleDeleteStudent(student.studentId)}>
              Delete
            </button>
            <button type="button" onClick={() => handleEditStudent(student)}>
              Edit
            </button>
          </li>
        ))}
      </ul>

      <h3>Add Student</h3>
      <input
        type="text"
        placeholder="Student Name"
        value={newStudentName}
        onChange={(e) => setNewStudentName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Student Email"
        value={newStudentEmail}
        onChange={(e) => setNewStudentEmail(e.target.value)}
      />
      <button type="button" onClick={handleAddStudent}>
        Add
      </button>

      {editedStudent && (
  <EditStudent
    student={editedStudent}
    onCancel={() => setEditedStudent(null)}
    onEdit={handleEditSubmit}
    onDelete={handleDeleteStudent} // Pass the onDelete function
  />
)}


      {message && <div>{message}</div>}
    </div>
  );
};

export default AdminHome;
